mapper.select:
		select p_no, p_name, p_address, p_info, p_phone, p_x, p_y, p_img_route 
    		from place_info po join place_img pg on po.p_no = pg.p_img_no
    		where p_no = #{p_no}

		select p_no, p_name, p_address, p_info, p_phone, p_x, p_y, p_img_route 
    		from place_info po join place_img pg on po.p_no = pg.p_img_no
    		where area_code = #{area_code}

mapper.update:
BEGIN
	update place_info 
	set p_no=#{p_no}

		, p_name=#{p_name}


		, p_info=#{p_info}


		, p_phone=#{p_phone}


		, p_address=#{p_address}, p_x=#{p_x}, p_y=#{p_y}, area_code=#{area_code}

		where p_no=#{p_no};


		update place_img
		set p_img_origin=#{p_img_origin}, p_img_save=#{p_img_save}, p_img_route=#{p_img_route}
		where p_img_no=#{p_no};

END;

mapper.select:
select count(*) from place_info po where po.p_name LIKE '%'||#{studyCafe_keyword}||'%'
and po.area_code in (select a.AREA_CODE from AREA a where a.SIDO_NAME = #{sido_name})
and po.area_code = #{area_code}

select p_no, p_name, p_address, p_info, p_phone, p_x, p_y, p_img_route from place_info po join place_img pg on po.p_no = pg.p_img_no where po.p_name LIKE '%'||#{studyCafe_keyword}||'%'
and po.area_code in (select a.AREA_CODE from AREA a where a.SIDO_NAME = #{sido_name})
and po.area_code = #{area_code}
